  font-size: 20px;
  font-weight: normal;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.6;
  letter-spacing: normal;
  text-align: center;
  color: #111;
`;
export const Image = styled.img`
  
  &.h{
    position: absolute;
  width: 146px;
  height: 114px;
  left: 30px;
  top: 0px; 
  }
  &.video-object{

  position: absolute;
  width: 520px;
  height: 471px;
  left: 30px;
  top: 0px; 
  }
`;
export const Button = styled.button`
  top: 20%;
