import styled from "styled-components";
export const BannerWrapper = styled.div`
  width: 100%;
  height: 1000px;
  position: relative;
  background-color: #e86e1c;
`;
export const Header = styled.div`
  top:10px;
  margin: 0 29px 49px;
  font-family: Montserrat;
  font-size: 50px;
  font-weight: bold;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.2;
  letter-spacing: 5px;
  text-align: center;
  color: #f96400;
  position: absolute;
`;
export const ImageBanner = styled.img`
  width: 100%;
  height: 100%;
`;
