import styled from "styled-components";

export const IntroBannerWrapper = styled.div`
  background-color: white;
  width: 100%;
  height: 1143px;
  position: relative;
`;
export const Header = styled.div`
  position: absolute;
  top: 10%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-transform: uppercase;
  font-family: Montserrat;
  font-size: 50px;
  font-weight: 800;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.2;
  letter-spacing: 10px;
  text-align: left;
  color: #111;
`;
export const Image = styled.img`
  position: absolute;
  width: 146px;
  height: 114px;
  left: 30px;
  top: 0px;
`;
