import styled from "styled-components";

export const IssueBannerWrapper = styled.div`
  background-color: #fccb05;
  width: 100%;
  height: 1143px;
  position: relative;
`;
export const Header = styled.div`
  position: absolute;
  top: 10%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-transform: uppercase;
  font-family: Montserrat;
  font-size: 45px;
  font-weight: bold;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.33;
  letter-spacing: 4.5px;
  text-align: left;
  color: #110e03;
`;

export const Content = styled.div`
  top: 15%;
  left: 50%;
  transform: translate(-50%, -50%);
  margin-top: 30px;
  position: absolute;
  font-family: NotoSansCJKkr;
  font-size: 16px;
  font-weight: 300;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.88;
  letter-spacing: normal;
  text-align: center;
  color: #fcd32a;
`;


export const HeaderIssue= styled.div`
  font-family: Montserrat;
  font-size: 25px;
  font-weight: 800;
  font-stretch: normal;
  font-style: normal;
  line-height: 2.4;
  letter-spacing: normal;
  text-align: left;
  color: #222;
  

`;
